Method.Implementation:
s unit="B"
s factor=$s(binary=0:1000,1:1024)
s mag=0


//While size>100 {
//While size'<factor {
; vzdy max 4 cislice 
While size>999.9 {
  s size=size/factor
  s mag=mag+1
}
s unit=$case(mag,0:"",1:"K",2:"M",3:"G",4:"T",5:"P")
if (binary && mag) s unit=unit_"i"

//s size=$num(size,2)
s size=$num(size,1) ; 1 desenine misto
s:$e(size)="." size="0"_size

q size_" "_unit_"B"

  if ictx="" {
    s callBack=##class(i2.init).getCallBack($$$GETICTX,"uploader")
  } else {
	; 27.03.25 ln; podpora S3
	; userRepo() nesmi v tomtoo pripade zaviset na inicializaci ipac
    s callBack=$g(^IpacCallback("c","uploader",ictx))
    ; 18.09.24 ln; podpora dedeni z cav
    if (callBack="") && ($g(^arlINHERITipref($zcvt(ictx,"W")_"UnTablesd"))'="") {
      q ictx_"repo"
    }
  }
  q:callBack="" ##class(content.uploader).userRepo()
  q:$f(callBack,".page404") ##class(content.uploader).userRepo()

  q $ZOBJCLASSMETHOD(callBack,"userRepo")

q:'##class(User.MARC).readX(.handle,$$$IPREF_"UnTablesd",stable)
s:node="" node=stable s c=0 do { s line=##class(User.MARC).getTagX(.handle,"200",.c) s tagA=##class(User.MARC).getSubTagStr(.line,"a") continue:tagA=""
//s tagB=$$$ls(##class(User.MARC).getSubTagStr(.line,"b")) ; 03.09.18 ln; podpora pro jazyky s tagB=$p((##class(User.MARC).getSubTagStr(.line,"b")),"#",$$$GETLANGUAGE) s:tagB="" tagB=tagA s data(node,tagA)=tagB } while (c'=0)
